<?php$loginerr='';if ($action = 'logout' && $uid && $username)//退出系统{	$adminitem=array();	$groupid=0;	setcookie('sessionid',null);	$sessionid=getRandStr(10,false);	$DB->query('UPDATE `'.DB_PREFIX."user` SET `sessionid` = '$sessionid' WHERE `uid` = '$uid'");	ob_end_clean();	ob_start();	//include(SYS_CORE.'/manager/view/header.php');//为何要包含这个捏？？？？}$lusername=$lpassword='';if (SYS_POST){	$lusername=$_POST['username'];	$lpassword=$_POST['password'];	$sql='SELECT * FROM `'.DB_PREFIX."user` WHERE `username` = '$lusername'";	$result=$DB->fetch_first($sql);	if ($result)	{		if ($result['password'] == md5($lpassword))		{			$uid = $result['uid'];			if ($result['groupid'] == 1) $loginerr = '您是注册会员,没有权限登陆后台';			elseif ($result['groupid'] < 4 && $result['hostid'] != $hostid) $loginerr='您无权限登陆该网站后台';//不是创始人,只能登陆一个站点			else			{				$sessionid=getRandStr(30,false);//生成登陆会话Session信息				$expire =isset($_POST['rememberme']) ? $timestamp+31536000 : 0;//过期时间设置，记住我为最长时间，否则为浏览器关闭则无效				setcookie('sessionid', $sessionid, $expire);				//更新用户登录信息				$DB->query('UPDATE `'.DB_PREFIX."user` SET `logincount` = `logincount`+1, `loginip` = '$onlineip', `logintime` = '$timestamp', `sessionid` = '$sessionid', `useragent` = '$useragent' WHERE `uid` = '$uid'");				//写入登录数据库日志				$DB->query('INSERT INTO `'.DB_PREFIX."login` (`hostid`, `user`, `dateline`, `useragent`, `ip`, `content`) values ('$hostid', '$lusername', '$timestamp', '$useragent', '$onlineip', '后台登录成功')");				//写入登录文件日志				writeLog(SYS_DATA.'/logs/login.log', $hostid."\t".$lusername."\t".$timestamp."\t".$useragent."\t".$onlineip."\t".'后台登录成功');				$adminurl = mkUrl('admin.php','');				redirect('登陆成功', $adminurl);			}		}		else $loginerr='密码错误';	}	else $loginerr='不存在的用户名';	//写入登录数据库日志	$DB->query('INSERT INTO `'.DB_PREFIX."login` (`hostid`, `user`, `dateline`, `useragent`, `ip`, `content`) VALUES ('$hostid', '$lusername', '$timestamp', '$useragent', '$onlineip', '$loginerr')");	//写入登录文件日志	writeLog(SYS_DATA.'/logs/login.log', $hostid."\t".$lusername."\t".$timestamp."\t".$useragent."\t".$onlineip."\t".$loginerr);	}if ($loginerr) $loginerr='<font color="red">'.$loginerr.'</font>';if ($groupid<2) $file='login';//没有登陆状态else $file='main';